// Modules
var express = require('express');
var app = express();

// Define port
var port = 3000;

// View engine
app.set('view engine', 'jade');

// Set public folder
app.use(express.static(__dirname + '/public'));

// Rest
var rest = require("../../index.js")(app);
rest.addDevice('xbee','/dev/tty.usbserial-A702LF8B');

// Start server
app.listen(port);
console.log("Listening on port " + port);